import { CircularProgress } from '@mui/material'
import React from 'react'
import styles from "./Section.module.css"
import { Card } from '../Card/Card'
import Carousel from '../Carousel/Carousel'
import BasicTabs from "../Tabs/Tabs"
import Box from '@mui/material/Box';

export const Section = ({data,title,type,filteredData=null,filteredDataValues=[],toggle=false,handleToggle=null,value=0,handleChange=null}) => {
  return (
    <div>
       <div className={styles.header}>
        <h3>{title}</h3>
        <h4 className={styles.toggleText} onClick={handleToggle}>
            {!toggle? "Collapse All":"Show All"}
        </h4>
      </div>
      {
        type==="song"? <BasicTabs value={value} handleChange={handleChange}/>:null
      }
      {
        data.length===0?(
            <Box sx={{display:"flex",justifyContent:"center",alignItems:"center"}}>
            <CircularProgress/ >
            </Box>
        ):(
            <div className={styles.cardWrapper}>
                {!toggle?(
                    <div className={styles.wrapper}>
                        {filteredDataValues.map((ele)=>
                            <Card data={ele} type={type}></Card>
                        )}
                    </div>
                ):(
                    <Carousel data={filteredDataValues} renderComponent={(data)=> <Card data={data} type={type}></Card>}></Carousel>
                )}
            </div>
        )
      }
    </div>
  )
}
